import { useState } from 'react';

const CounterButtons = () => {

    return (
        <div className="row">
            <span className="col-12">
                Current Count: {count}
            </span>
            <div className="col-3">
                <button
                    type="button"
                    className="btn btn-primary"
                    onClick={() => setCount(count + 1)}
                >
                    Increment
                </button>
                <button
                    type="button"
                    className="btn btn-primary"
                    onClick={() => setCount(count - 1)}
                >
                    Decerement
                </button>
            </div>
        </div>
    );
}
export default CounterButtons;
