import { useState } from 'react';

const CounterButtons = () => {
    const [count, setCount] = useState(0);
    return (
        <div>
            <span> Current Count: {count} </span>
            <button onClick={() => setCount(count + 1)}>
increment
            </button>{''}
            
        </div>
    );
}
export default CounterButtons;
